using System.Web;
using System.IO;
using System.Threading;
using System.Data.SqlClient;
using System;
using SuperSecureBank;
namespace SuperSecureBank
{
	public class DoTransfer : System.Web.UI.Page
	{
		protected void Page_Load(object sender, EventArgs e)
		{
			try {
				AccountMgmt.Transfer(Convert.ToInt64(Request.Params["FromAccount"]), Convert.ToInt64(Request.Params["ToAccount"]), Convert.ToInt64(Request.Params["Amount"]));
				Response.Redirect("ActionDone.aspx?Title=Transfer Success&Text=Your transfer was successful. If you moved funds within SuperSecure Bank accounts your funds are immediately available.");
			} catch (ThreadAbortException tae) {
			} catch (Exception ex) {
				ErrorLogging.AddException("Error in " + Path.GetFileName(Request.PhysicalPath), ex);
				Response.Redirect("ActionDone.aspx?Title=Transfer Failed&Text=We're sorry, but there was an error transferring your funds. Please try again at a later date or call support at: 1-800-555-1212");
			}
		}
	}
	public class AccountMgmt
	{
		public static void Transfer(Int64 FromAccount, Int64 ToAccount, Int64 Amount)
		{
			try {
				Int64 FromAccountBalance = GetBalance(FromAccount);
				Int64 ToAccountBalance = GetBalance(ToAccount);
				UpdateBalance(ToAccount, ToAccountBalance + Amount);
				Thread.Sleep(new Random().Next(1000, 5000));
				UpdateBalance(FromAccount, FromAccountBalance - Amount);
			} catch {
				throw;
			}
		}
		public static Int64 GetBalance(Int64 accountID)
		{
			string selectAccount = "SELECT balance FROM Accounts WHERE accountID = {0}";
			Int64 balance = 0;
			try {
				using (SqlConnection conn = new SqlConnection(ConfigurationManager.ConnectionStrings["ssbcon"].ConnectionString)) {
					conn.Open();
					selectAccount = String.Format(selectAccount, accountID);
					SqlCommand command = new SqlCommand(selectAccount, conn);
					SqlDataReader reader = command.ExecuteReader();
					while (reader.Read()) {
						balance = reader.GetInt64(0);
					}
				}
			} catch {
				throw;
			}
			return balance;
		}
		public static void UpdateBalance(Int64 Account, Int64 NewAmount)
		{
			try {
				string updateAmount = "UPDATE Accounts SET balance={0} WHERE accountID = {1}";
				using (SqlConnection conn = new SqlConnection(ConfigurationManager.ConnectionStrings["ssbcon"].ConnectionString)) {
					conn.Open();
					updateAmount = String.Format(updateAmount, NewAmount, Account);
					SqlCommand command = new SqlCommand(updateAmount, conn);
					command.ExecuteNonQuery();
				}
			} catch {
				throw;
			}
		}
	}
	public class ErrorLogging
	{
		public static void AddException(string errorText, Exception ex)
		{
			string insertError = "INSERT INTO ErrorLog values ('{0}', '{1}', '{2}')";
			SqlConnection conn = new SqlConnection(ConfigurationManager.ConnectionStrings["ssbcon"].ConnectionString);
			conn.Open();
			insertError = String.Format(insertError, DateTime.Now, cleanForSQL(errorText), BuildExceptionText(ex));
			SqlCommand command = new SqlCommand(insertError, conn);
			command.ExecuteNonQuery();
			conn.Close();
		}
		public static string cleanForSQL(string foo)
		{
			if (null != foo) {
				return foo.Replace("'", "''");
			} else
				return "";
		}
		private static string BuildExceptionText(Exception ex)
		{
			string exceptionText = "<h3>{0}</h3>More Info: {1}<br/><h4>Stack</h4><br/><pre><code>{2}</code></pre>";
			return string.Format(exceptionText, cleanForSQL(ex.Message), cleanForSQL(ex.HelpLink), cleanForSQL(ex.StackTrace.Replace("\r\n", "<br>").Replace("\t", "&nbsp;&nbsp;&nbsp;&nbsp;")));
		}
	}
}
